import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import java.util.logging.Logger
import java.sql.Date
import java.text.SimpleDateFormat

// Logger global
def LOG = Logger.getLogger("ExecuteScript")

// Liste des champs  traiter (sans chiffrement)
def SENSITIVE_FIELDS = ["per_capita_income", "yearly_income", "total_debt"]

def flowFile = session.get()
if (flowFile != null) {
    try {
        flowFile = session.putAttribute(flowFile, "script.status", "starting")

        flowFile = session.write(flowFile, { inputStream, outputStream ->
            def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)

            LOG.info("Contenu reu : " + text)

            if (!text || text.trim().isEmpty()) {
                LOG.severe("Le texte d'entre est vide ou null")
                outputStream.write("{}".getBytes(StandardCharsets.UTF_8))
                return
            }

            // Nettoyage du texte
            text = text.trim()
            if (text.startsWith("[[") && text.endsWith("]]")) {
                text = text.substring(2, text.length() - 2).trim()
            } else if (text.startsWith("[") && text.endsWith("]")) {
                text = text.substring(1, text.length() - 1).trim()
            }

            // Parser JSON
            def data
            try {
                data = new groovy.json.JsonSlurper().parseText(text)
                LOG.info("JSON pars avec succs : " + data)
            } catch (Exception e) {
                LOG.severe("Erreur de parsing JSON : " + e.getMessage())
                LOG.severe("Texte qui a caus l'erreur : " + text)
                outputStream.write("{}".getBytes(StandardCharsets.UTF_8))
                return
            }

            if (data == null) {
                LOG.severe("Le JSON pars est null")
                outputStream.write("{}".getBytes(StandardCharsets.UTF_8))
                return
            }

            def result = [:]

            // Copier toutes les proprits
            data.each { key, value ->
                result[key] = value
            }

            // Traitement des dates d'expiration (si applicable)
            if (result.expires != null) {
                try {
                    String expiresStr = result.expires.toString().trim()
                    String[] parts = expiresStr.split("-")

                    if (parts.length == 2) {
                        String day = parts[0]
                        String month = parts[1]

                        if (day.length() <= 2 && month.length() >= 3) {
                            String year = "20" + day
                            String monthNum = getMonthNumber(month)
                            result.expires = year + "-" + monthNum + "-01"
                        } else {
                            String monthNum = getMonthNumber(day)
                            String year = "20" + month
                            result.expires = year + "-" + monthNum + "-01"
                        }
                    } else {
                        LOG.warning("Format de date d'expiration non reconnu: " + expiresStr)
                    }
                } catch (Exception e) {
                    LOG.warning("Erreur lors de la conversion de la date d'expiration : " + e.getMessage())
                }
            }

            // Conversion des dates d'ouverture de compte
            if (result.acct_open_date != null) {
                try {
                    // Format attendu: D-MMM (par exemple 3-Jul)
                    String dateStr = result.acct_open_date.toString().trim()
                    String[] parts = dateStr.split("-")

                    if (parts.length >= 2) {
                        String day = parts[0].padLeft(2, '0') // Jour avec zro initial si ncessaire
                        String monthNum = getMonthNumber(parts[1])
                        String year

                        // Si nous avons une anne (format D-MMM-YY)
                        if (parts.length > 2) {
                            year = "20" + parts[2]
                        } else {
                            // Utiliser l'anne en cours si non spcifie
                            year = new SimpleDateFormat("yyyy").format(new Date())
                        }

                        // Format final YYYY-MM-DD
                        String formattedDate = year + "-" + monthNum + "-" + day
                        result.acct_open_date = new Date(new SimpleDateFormat("yyyy-MM-dd").parse(formattedDate).getTime())
                    } else {
                        LOG.warning("Format de date d'ouverture non reconnu: " + dateStr)
                    }
                } catch (Exception e) {
                    LOG.warning("Erreur lors de la conversion de la date d'ouverture : " + e.getMessage())
                }
            }

            // Conversion du crdit limit
            if (result.credit_limit != null) {
                try {
                    result.credit_limit = result.credit_limit.toString().replace('$', '').replace(',', '').trim()
                } catch (Exception e) {
                    LOG.warning("Erreur lors de la conversion de la limite de crdit : " + e.getMessage())
                }
            }

            // Pour has_chip
            if (result.has_chip != null) {
                result.has_chip = (result.has_chip.toString().toUpperCase() == "YES")
            }

            // Pour card_on_dark_web
            if (result.card_on_dark_web != null) {
                result.card_on_dark_web = !(result.card_on_dark_web.toString().toUpperCase() == "NO")
            }

            // Masquer les champs sensibles sans chiffrement
            SENSITIVE_FIELDS.each { field ->
                if (result[field] != null) {
                    try {
                        // Sauvegarder une version masque pour l'affichage
                        result["masked_" + field] = "***"
                    } catch (Exception e) {
                        LOG.warning("Erreur lors du masquage du champ " + field + " : " + e.getMessage())
                    }
                }
            }

            // Journal pour dboguer
            LOG.info("Date d'expiration formate: " + result.expires)
            LOG.info("Date d'ouverture formate: " + result.acct_open_date)
            LOG.info("Champs sensibles masqus")

            def json = new groovy.json.JsonBuilder(result).toPrettyString()
            outputStream.write(json.getBytes(StandardCharsets.UTF_8))
        })

        flowFile = session.putAttribute(flowFile, "script.status", "success")
        session.transfer(flowFile, REL_SUCCESS)
    } catch (Exception e) {
        LOG.severe("Exception dans le script: " + e.getMessage())
        e.printStackTrace()
        flowFile = session.putAttribute(flowFile, "script.error", e.getMessage())
        flowFile = session.putAttribute(flowFile, "script.error.stacktrace", e.toString())
        session.transfer(flowFile, REL_FAILURE)
    }
}

def getMonthNumber(monthAbbr) {
    def months = [
        'Jan': '01', 'Feb': '02', 'Mar': '03', 'Apr': '04', 'May': '05', 'Jun': '06',
        'Jul': '07', 'Aug': '08', 'Sep': '09', 'Oct': '10', 'Nov': '11', 'Dec': '12'
    ]
    return months[monthAbbr.toString()] ?: '01'
}

// Mthode d'extension pour ajouter des 0 devant un nombre
String.metaClass.padLeft = { int width, String padding ->
    def result = delegate
    while (result.length() < width) {
        result = padding + result
    }
    return result
}
