import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import java.util.logging.Logger
import java.text.SimpleDateFormat
import javax.crypto.Cipher
import javax.crypto.spec.SecretKeySpec
import javax.crypto.spec.IvParameterSpec
import java.util.Base64
import java.sql.Date
import java.text.SimpleDateFormat


// Logger global
def LOG = Logger.getLogger("ExecuteScript")

// Clé de chiffrement et vecteur d'initialisation (à stocker de manière sécurisée dans un environnement de production)
def ENCRYPTION_KEY = "MySecretKey12345" // À remplacer par une clé sécurisée de 16 caractères
def ENCRYPTION_IV = "InitializationVe" // À remplacer par un IV sécurisé de 16 caractères

// Liste des champs à chiffrer
def SENSITIVE_FIELDS = ["card_number", "cvv"]

def flowFile = session.get()
if (flowFile != null) {
    try {
        flowFile = session.putAttribute(flowFile, "script.status", "starting")

        flowFile = session.write(flowFile, { inputStream, outputStream ->
            def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)

            LOG.info("Contenu reçu : " + text)

            if (!text || text.trim().isEmpty()) {
                LOG.severe("Le texte d'entrée est vide ou null")
                outputStream.write("{}".getBytes(StandardCharsets.UTF_8))
                return
            }

            // Nettoyage du texte
            text = text.trim()
            if (text.startsWith("[[") && text.endsWith("]]")) {
                text = text.substring(2, text.length() - 2).trim()
            } else if (text.startsWith("[") && text.endsWith("]")) {
                text = text.substring(1, text.length() - 1).trim()
            }

            // Parser JSON
            def data
            try {
                data = new groovy.json.JsonSlurper().parseText(text)
                LOG.info("JSON parsé avec succès : " + data)
            } catch (Exception e) {
                LOG.severe("Erreur de parsing JSON : " + e.getMessage())
                LOG.severe("Texte qui a causé l'erreur : " + text)
                outputStream.write("{}".getBytes(StandardCharsets.UTF_8))
                return
            }

            if (data == null) {
                LOG.severe("Le JSON parsé est null")
                outputStream.write("{}".getBytes(StandardCharsets.UTF_8))
                return
            }

            def result = [:]

            // Copier toutes les propriétés
            data.each { key, value ->
                result[key] = value
            }

            // Traitement des dates d'expiration
            if (result.expires != null) {
                try {
                    String expiresStr = result.expires.toString().trim()
                    String[] parts = expiresStr.split("-")

                    if (parts.length == 2) {
                        String day = parts[0]
                        String month = parts[1]

                        if (day.length() <= 2 && month.length() >= 3) {
                            String year = "20" + day
                            String monthNum = getMonthNumber(month)
                            result.expires = year + "-" + monthNum + "-01"
                        } else {
                            String monthNum = getMonthNumber(day)
                            String year = "20" + month
                            result.expires = year + "-" + monthNum + "-01"
                        }
                    } else {
                        LOG.warning("Format de date d'expiration non reconnu: " + expiresStr)
                    }
                } catch (Exception e) {
                    LOG.warning("Erreur lors de la conversion de la date d'expiration : " + e.getMessage())
                }
            }

            // Conversion des dates d'ouverture de compte
            if (result.acct_open_date != null) {
                try {
                    // Format attendu: D-MMM (par exemple 3-Jul)
                    String dateStr = result.acct_open_date.toString().trim()
                    String[] parts = dateStr.split("-")

                    if (parts.length >= 2) {
                        String day = parts[0].padLeft(2, '0') // Jour avec zéro initial si nécessaire
                        String monthNum = getMonthNumber(parts[1])
                        String year

                        // Si nous avons une année (format D-MMM-YY)
                        if (parts.length > 2) {
                            year = "20" + parts[2]
                        } else {
                            // Utiliser l'année en cours si non spécifiée
                            year = new SimpleDateFormat("yyyy").format(new Date())
                        }

                        // Format final YYYY-MM-DD
                        String formattedDate = year + "-" + monthNum + "-" + day
                        result.acct_open_date = new Date(new SimpleDateFormat("yyyy-MM-dd").parse(formattedDate).getTime())


                    } else {
                        LOG.warning("Format de date d'ouverture non reconnu: " + dateStr)
                    }
                } catch (Exception e) {
                    LOG.warning("Erreur lors de la conversion de la date d'ouverture : " + e.getMessage())
                }
            }

            // Conversion du crédit limit
            if (result.credit_limit != null) {
                try {
                    result.credit_limit = result.credit_limit.toString().replace('$', '').replace(',', '').trim()
                } catch (Exception e) {
                    LOG.warning("Erreur lors de la conversion de la limite de crédit : " + e.getMessage())
                }
            }

            // Pour has_chip
            if (result.has_chip != null) {
                result.has_chip = (result.has_chip.toString().toUpperCase() == "YES")
            }

            // Pour card_on_dark_web
            if (result.card_on_dark_web != null) {
                result.card_on_dark_web = !(result.card_on_dark_web.toString().toUpperCase() == "NO")
            }

            // Masquer et chiffrer les champs sensibles
            SENSITIVE_FIELDS.each { field ->
                if (result[field] != null) {
                    try {
                        // Sauvegarder une version masquée pour l'affichage
                        if (field == "card_number") {
                            def cardNum = result[field].toString()
                            if (cardNum.contains("E+")) {
                                def parts = cardNum.split("E\\+")
                                def base = Double.parseDouble(parts[0])
                                def exponent = Integer.parseInt(parts[1])
                                def fullNumber = String.format("%.0f", base * Math.pow(10, exponent))
                                result["masked_" + field] = 'XXXXXXXXXXXX' + fullNumber.substring(Math.max(0, fullNumber.length() - 4))
                            } else {
                                result["masked_" + field] = 'XXXXXXXXXXXX' + cardNum.substring(Math.max(0, cardNum.length() - 4))
                            }
                        } else {
                            result["masked_" + field] = "***"
                        }

                        // Chiffrer la valeur réelle
                        result[field] = encrypt(result[field].toString(), ENCRYPTION_KEY, ENCRYPTION_IV)
                    } catch (Exception e) {
                        LOG.warning("Erreur lors du chiffrement du champ " + field + " : " + e.getMessage())
                    }
                }
            }

            // Journal pour déboguer
            LOG.info("Date d'expiration formatée: " + result.expires)
            LOG.info("Date d'ouverture formatée: " + result.acct_open_date)
            LOG.info("Champs sensibles chiffrés")

            def json = new groovy.json.JsonBuilder(result).toPrettyString()
            outputStream.write(json.getBytes(StandardCharsets.UTF_8))
        })

        flowFile = session.putAttribute(flowFile, "script.status", "success")
        session.transfer(flowFile, REL_SUCCESS)
    } catch (Exception e) {
        LOG.severe("Exception dans le script: " + e.getMessage())
        e.printStackTrace()
        flowFile = session.putAttribute(flowFile, "script.error", e.getMessage())
        flowFile = session.putAttribute(flowFile, "script.error.stacktrace", e.toString())
        session.transfer(flowFile, REL_FAILURE)
    }
}

def getMonthNumber(monthAbbr) {
    def months = [
        'Jan': '01', 'Feb': '02', 'Mar': '03', 'Apr': '04', 'May': '05', 'Jun': '06',
        'Jul': '07', 'Aug': '08', 'Sep': '09', 'Oct': '10', 'Nov': '11', 'Dec': '12'
    ]
    return months[monthAbbr.toString()] ?: '01'
}

// Méthode d'extension pour ajouter des 0 devant un nombre
String.metaClass.padLeft = { int width, String padding ->
    def result = delegate
    while (result.length() < width) {
        result = padding + result
    }
    return result
}

// Fonction de chiffrement AES
def encrypt(String plainText, String key, String iv) {
    try {
        // S'assurer que la clé et l'IV font 16 caractères (128 bits)
        key = key.padRight(16).substring(0, 16)
        iv = iv.padRight(16).substring(0, 16)

        Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding")
        SecretKeySpec keySpec = new SecretKeySpec(key.getBytes("UTF-8"), "AES")
        IvParameterSpec ivSpec = new IvParameterSpec(iv.getBytes("UTF-8"))

        cipher.init(Cipher.ENCRYPT_MODE, keySpec, ivSpec)
        byte[] encryptedBytes = cipher.doFinal(plainText.getBytes("UTF-8"))

        return Base64.getEncoder().encodeToString(encryptedBytes)
    } catch (Exception e) {
        LOG.severe("Erreur de chiffrement: " + e.getMessage())
        return plainText // En cas d'erreur, retourne le texte non chiffré mais logge l'erreur
    }
}

// Fonction de déchiffrement (pour référence)
def decrypt(String encryptedText, String key, String iv) {
    try {
        // S'assurer que la clé et l'IV font 16 caractères (128 bits)
        key = key.padRight(16).substring(0, 16)
        iv = iv.padRight(16).substring(0, 16)

        Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding")
        SecretKeySpec keySpec = new SecretKeySpec(key.getBytes("UTF-8"), "AES")
        IvParameterSpec ivSpec = new IvParameterSpec(iv.getBytes("UTF-8"))

        cipher.init(Cipher.DECRYPT_MODE, keySpec, ivSpec)
        byte[] decodedBytes = Base64.getDecoder().decode(encryptedText)
        byte[] decryptedBytes = cipher.doFinal(decodedBytes)

        return new String(decryptedBytes, "UTF-8")
    } catch (Exception e) {
        LOG.severe("Erreur de déchiffrement: " + e.getMessage())
        return encryptedText // En cas d'erreur, retourne le texte chiffré mais logge l'erreur
    }
}
